ifif-elsif-elseif-elseBooleancase <pre><code>CREATE OR REPLACE PROCEDURE decision1()
LANGUAGE plpgsql
AS $$
DECLARE
    x numeric;
BEGIN
    SELECT sal INTO x 
    FROM emp20 
    WHERE ename = 'King';

    IF x &gt;= 5000 THEN
        INSERT INTO tempp VALUES (x, 'High Sal');
    END IF;
END;
$$;

-- call
call decision1();
</code></pre> <pre><code>CREATE OR REPLACE PROCEDURE decision2()
LANGUAGE plpgsql
AS $$
DECLARE
    x numeric;
BEGIN
    SELECT sal INTO x 
    FROM emp20 
    WHERE ename = 'King';

    IF x &gt; 5000 THEN
        INSERT INTO tempp VALUES (x, 'High Sal');
    elsif x &lt; 5000 then
        INSERT INTO tempp VALUES (x, 'Low Sal');
    else
        INSERT INTO tempp VALUES (x, 'Med Sal');

    END IF;
END;
$$;

-- call
call decision2();
</code></pre> <pre><code>CREATE OR REPLACE PROCEDURE decision3()
