</code></pre> <pre><code>CREATE OR REPLACE PROCEDURE salary_case()
LANGUAGE plpgsql
AS $$
DECLARE
    sal numeric;
    remark text;
BEGIN
    -- Get salary of KING
    SELECT emp20.sal INTO sal
    FROM emp20
    WHERE ename ILIKE 'King';

    -- Use CASE to categorize salary
    remark := CASE
                WHEN sal &gt; 5000 THEN 'High Salary'
                WHEN sal BETWEEN 3000 AND 4999 THEN 'Medium Salary'
                ELSE 'Low Salary'
            END;

    INSERT INTO tempp VALUES (sal, remark);
END;
$$;
-- call
call salary_case();
